var cekilenler=[],header=document.getElementById("header-sayi");function init(){for(var e=document.getElementById("root"),n=0;n<90;n++){var t=n+1,i=`<div class="col-2 col-sm-1 col-md-1 col-lg-1 col-xl-1 d-flex justify-content-center block"><div id="coin-${t}" class="circle shadow bg-secondary text-light"><p>${t}</p></div></div>`;e.innerHTML=e.innerHTML+i}}function update(e){if(header.parentElement.classList.toggle("header-sayi",!0),e>90||e<1||cekilenler.includes(e))yeniCek();else{header.innerHTML=e,cekilenler.push(e);var n=document.getElementById(`coin-${e}`);n.classList.remove("bg-secondary","text-light"),n.classList.add("bg-warning","text-dark")}}function generateNumber(e,n){return Math.round(Math.random()*(n-e))+e}function openModal(){var e=new bootstrap.Modal(document.getElementById("karistir"),{keyboard:!1});e.show();var n=generateNumber(3,5);setTimeout(function(){e.hide(),responsiveVoice.speak(`${cekilenler[cekilenler.length-1]}`,"Turkish Male")},1e3*n)}function yeniCek(){update(generateNumber(-100,200))}function test(e){for(var n=0;;)if(n++,yeniCek(),cekilenler.includes(e))return void console.log(e+" -> "+n+". denemede bulundu.")}document.getElementById("sayi-cek").addEventListener("click",function(){openModal(),setTimeout(function(){yeniCek()},2e3)}),init();